import React, { useState } from 'react';
import { View, Text, Button, Image, StyleSheet } from 'react-native';

// Lista de imagens e seus t√≠tulos correspondentes
const imagens = [
  { title: 'Imagem 1' , src: 'data:image/jpeg;base64,'},
  { title: 'Imagem 2' , src: 'data:image/jpeg;base64,'},
  { title: 'Imagem 3' , src: 'data:image/jpeg;base64,'},
  { title: 'Imagem 4' , src: 'data:image/jpeg;base64,'},
];

export default function App() {
  const [imagemAtual, setImagemAtual] = useState(imagens[0]);

  const trocarImagem = () => {
    const imagemAleatoria = imagens[Math.floor(Math.random() * imagens.length)];
    setImagemAtual(imagemAleatoria);
  };

  return (
    <View style={styles.container}>
      <Image source={{ uri: imagemAtual.src }} style={styles.imagem} />
      <Text style={styles.titulo}>{imagemAtual.title}</Text>
      <Button title="Trocar Imagem" onPress={trocarImagem} />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
  },
  imagem: {
    width: 150,
    height: 150,
  },
  titulo: {
    marginBottom: 20,
  },
});
